  import connector.common.JsonUtil;
  log.error("======================== FROM BERORE START ==================");

  // Get the body content of the HTTP request

  Map bodyMap = requestEndPoint.getBody();

  // Extract the JSON body content from the body map

  String bodyStr = bodyMap.get("jsonBody");

  // Log the original body content

  log.error("Original JSON Body:\n" + bodyStr);

  // Create a new JSON array with an object containing roleId and productId properties
  String ft = provisioningPlan.getNativeIdentity();

  String newJson = "[{\"firstName\":"+ ft +"}]";

  // Update the body map with the new JSON content

  bodyMap.put("jsonBody", newJson);

  requestEndPoint.setBody(bodyMap);

  // Log the request end point with the updated JSON content

  log.error("Request End Point after updating body:\n" + requestEndPoint);
  log.error("======================== FROM BERORE  END ==================");
  // Return the updated request end point

  return requestEndPoint;
